const $=document.querySelector.bind(document),input=$(".search-input"),searchBtn=$(".search-btn");searchBtn.addEventListener("click",()=>{fetch(`https://api.weatherapi.com/v1/current.json?key=63b5ec3c30444ae08f0130656220204&q=${input.value}&aqi=no`).then(t=>t.json()).then(t=>{$(".search input").value="",$(".search input").focus(),$(".weather-img").src=t.current.condition.icon,$(".weather-heading").textContent=t.location.name+" - "+t.location.country,$(".weather-desc").textContent=t.current.condition.text,$(".weather-temp").textContent=t.current.temp_c+"°C",$(".weather-humidity p").textContent=t.current.humidity+"%",$(".weather-wind-speed p").textContent=t.current.wind_kph+" km/h",$(".weather-gust p").textContent=t.current.wind_kph+" km/h",$(".weather-vis p").textContent=t.current.vis_km+" km",$(".weather-wind-degree p").textContent=t.current.wind_degree+"°",$(".weather-wind-dir p").textContent=t.current.wind_dir,$(".weather-pressure p").textContent=t.current.pressure_mb+" mb",$(".weather-precip p").textContent=t.current.precip_mm+" mm",$(".weather-last-updated p").textContent=t.current.last_updated}).catch(t=>t)});