const $=document.querySelector.bind(document),input=$(".search-input"),searchBtn=$(".search-btn");searchBtn.addEventListener("click",()=>{fetch(`https://api.weatherapi.com/v1/current.json?key=63b5ec3c30444ae08f0130656220204&q=${input.value}&aqi=no`).then(e=>e.json()).then(e=>{$(".search input").value="",$(".search input").focus(),$(".weather-img").src=e.current.condition.icon,$(".weather-heading").textContent=e.location.name+" - "+e.location.country,$(".weather-desc").textContent=e.current.condition.text,$(".weather-temp").textContent=e.current.temp_c+"°C / "+e.current.temp_f+"°F",$(".weather-humidity p").textContent=e.current.humidity+"%",$(".weather-wind-speed p").textContent=e.current.wind_kph+" km/h - "+e.current.wind_mph+" mph",$(".weather-gust p").textContent=e.current.wind_kph+" km/h - "+e.current.wind_mph+"mph",$(".weather-vis p").textContent=e.current.vis_km+" km - "+e.current.vis_miles+" miles",$(".weather-wind-degree p").textContent=e.current.wind_degree+"°",$(".weather-wind-dir p").textContent=e.current.wind_dir,$(".weather-pressure p").textContent=e.current.pressure_mb+" mb - "+e.current.pressure_in+" in",$(".weather-precip p").textContent=e.current.precip_mm+" mm - "+e.current.precip_in+" in",$(".weather-last-updated p").textContent=e.current.last_updated}).catch(e=>e)});